import React, { useState } from 'react';

function App() {
  const [round, setRound] = useState(0);
  const [table_pot, setTablePot] = useState(0);
  const [balance, setBalance] = useState(0);
  const [min_bet, setMinBet] = useState(1);

  // Function to handle deposit
  const handleDeposit = () => {
    // Increase the balance by the minimum bet
    setBalance(balance + min_bet);
  };

  // Function to handle betting the minimum
  const handleBet = () => {
    if (balance >= min_bet) {
      // Reduce the user's balance by the minimum bet
      setBalance(balance - min_bet);
      // Increase the table pot by the minimum bet
      setTablePot(table_pot + min_bet);
    } else {
      alert("Not enough balance to place a bet.");
    }
  };

  return (
    <div className="app">
      <div className="search">
        <div>
          <h4>Guavita</h4>
        </div>
      </div>
      <div className="container">
        <div className="top">
          <div className="">
            <p>Balance: {balance}</p>
          </div>
          <div className="">
            <p>Minimum Bet: {min_bet}</p>
          </div>
          <div className="">
            <p>Table Pot: {table_pot}</p>
          </div>
        </div>

        {/* Button to deposit */}
        <button onClick={handleDeposit}>Deposit</button>

        {/* Button to bet the minimum */}
        <button onClick={handleBet}>Bet Minimum</button>
      </div>
    </div>
  );
}

export default App;
